    public static HBItem getNum(List<RecordsBean.MonitoringDataListBean> b) {
        HBItem hbItem = new HBItem();
        for (int i = 0; i < b.size(); i++) {
            hbItem.isException  = b.get(i).isException;
            switch (b.get(i).pollutantCode) {
                case "w21003":
                    hbItem.ammoniacalData = String.valueOf(b.get(i).realTimeData);
                    break;
                case "w21011":
                    hbItem.totalData = String.valueOf(b.get(i).realTimeData);
                    break;
                case "w01018":
                    hbItem.coddata = String.valueOf(b.get(i).realTimeData);
                    break;
                case "a34013":
                    hbItem.smokeData = String.valueOf(b.get(i).realTimeData);
                    break;
                case "a21026":
                    hbItem.sulfurData = String.valueOf(b.get(i).realTimeData);
                    break;
                case "a21002":
                    hbItem.nitrogenData = String.valueOf(b.get(i).realTimeData);
                    break;
            }
        }

        return hbItem;
    }
